import { prompt } from '../utils/index.js';
import { BaseVersion } from '@voiceflow/base-types';
export const defaultMessageDelay = ({ durationMilliseconds = 1000, } = {}) => ({
    durationMilliseconds,
});
export const defaultSettings = ({ error, messageDelay, ...baseSettings } = {}) => ({
    ...BaseVersion.defaultSettings(baseSettings),
    error: prompt.defaultPrompt(error),
    messageDelay: defaultMessageDelay(messageDelay),
});
