import fs from 'node:fs';
import path from 'node:path';
import { fileURLToPath } from 'node:url';
import mri from 'mri';
import * as prompts from '@clack/prompts';
import colors from 'picocolors';

const {
  blue,
  blueBright,
  cyan,
  green,
  magenta,
  red,
  redBright,
  yellow
} = colors;
const argv = mri(process.argv.slice(2), {
  alias: { h: "help", t: "template" },
  boolean: ["help", "overwrite"],
  string: ["template"]
});
const cwd = process.cwd();
const helpMessage = `Usage: create-silgi [OPTION]... [DIRECTORY]

Create a new Silgi project in JavaScript or TypeScript.
With no arguments, start the CLI in interactive mode.

Options:
  -t, --template NAME        use a specific template

Available templates:
${yellow("vanilla-ts     vanilla")}
${green("vue-ts         vue")}
${cyan("react-ts       react")}
${cyan("react-swc-ts   react-swc")}
${magenta("preact-ts      preact")}
${redBright("lit-ts         lit")}
${red("svelte-ts      svelte")}
${blue("solid-ts       solid")}
${blueBright("qwik-ts        qwik")}`;
const renameFiles = {
  _gitignore: ".gitignore"
};
const defaultTargetDir = "silgi-project";
async function init() {
  const argTargetDir = argv._[0] ? formatTargetDir(String(argv._[0])) : void 0;
  const argOverwrite = argv.overwrite;
  const argTemplate = argv.template;
  const help = argv.help;
  if (help) {
    console.log(helpMessage);
    return;
  }
  const pkgInfo = pkgFromUserAgent(process.env.npm_config_user_agent);
  const cancel = () => prompts.cancel("Operation cancelled");
  let targetDir = argTargetDir;
  if (!targetDir) {
    const projectName = await prompts.text({
      message: "Project name:",
      defaultValue: defaultTargetDir,
      placeholder: defaultTargetDir
    });
    if (prompts.isCancel(projectName)) return cancel();
    targetDir = formatTargetDir(projectName);
  }
  if (fs.existsSync(targetDir) && !isEmpty(targetDir)) {
    const overwrite = argOverwrite ? "yes" : await prompts.select({
      message: (targetDir === "." ? "Current directory" : `Target directory "${targetDir}"`) + ` is not empty. Please choose how to proceed:`,
      options: [
        {
          label: "Cancel operation",
          value: "no"
        },
        {
          label: "Remove existing files and continue",
          value: "yes"
        },
        {
          label: "Ignore files and continue",
          value: "ignore"
        }
      ]
    });
    if (prompts.isCancel(overwrite)) return cancel();
    switch (overwrite) {
      case "yes":
        emptyDir(targetDir);
        break;
      case "no":
        cancel();
        return;
    }
  }
  let packageName = path.basename(path.resolve(targetDir));
  if (!isValidPackageName(packageName)) {
    const packageNameResult = await prompts.text({
      message: "Package name:",
      defaultValue: toValidPackageName(packageName),
      placeholder: toValidPackageName(packageName),
      validate(dir) {
        if (!isValidPackageName(dir)) {
          return "Invalid package.json name";
        }
      }
    });
    if (prompts.isCancel(packageNameResult)) return cancel();
    packageName = packageNameResult;
  }
  let template = argTemplate;
  const templatesDir = path.resolve(
    fileURLToPath(import.meta.url),
    "../../templates"
  );
  if (!fs.existsSync(templatesDir)) {
    fs.mkdirSync(templatesDir, { recursive: true });
    prompts.log.warn("No templates found. Please add template directories to: " + templatesDir);
  }
  const availableTemplates = fs.readdirSync(templatesDir).filter((dir) => fs.statSync(path.join(templatesDir, dir)).isDirectory());
  let hasInvalidArgTemplate = false;
  if (argTemplate && !availableTemplates.includes(argTemplate)) {
    template = void 0;
    hasInvalidArgTemplate = true;
  }
  if (!template) {
    if (availableTemplates.length === 0) {
      prompts.log.error("No template directories found in: " + templatesDir);
      prompts.log.info("Please add at least one template directory and try again.");
      process.exit(1);
    }
    const templateChoice = await prompts.select({
      message: hasInvalidArgTemplate ? `"${argTemplate}" isn't a valid template. Please choose from below: ` : "Select a template:",
      options: availableTemplates.map((templateName) => ({
        label: templateName,
        value: templateName
      }))
    });
    if (prompts.isCancel(templateChoice)) return cancel();
    template = templateChoice;
  }
  const root = path.join(cwd, targetDir);
  fs.mkdirSync(root, { recursive: true });
  const pkgManager = pkgInfo ? pkgInfo.name : "pnpm";
  prompts.log.step(`Scaffolding project in ${root}...`);
  const templateDir = path.join(templatesDir, template);
  const write = (file, content) => {
    const targetPath = path.join(root, renameFiles[file] ?? file);
    if (content) {
      fs.writeFileSync(targetPath, content);
    } else {
      copy(path.join(templateDir, file), targetPath);
    }
  };
  const files = fs.readdirSync(templateDir);
  for (const file of files.filter((f) => f !== "package.json")) {
    write(file);
  }
  const pkg = JSON.parse(
    fs.readFileSync(path.join(templateDir, `package.json`), "utf-8")
  );
  pkg.name = packageName;
  write("package.json", JSON.stringify(pkg, null, 2) + "\n");
  let doneMessage = "";
  const cdProjectName = path.relative(cwd, root);
  doneMessage += `Done. Now run:
`;
  if (root !== cwd) {
    doneMessage += `
  cd ${cdProjectName.includes(" ") ? `"${cdProjectName}"` : cdProjectName}`;
  }
  switch (pkgManager) {
    case "yarn":
      doneMessage += "\n  yarn";
      doneMessage += "\n  yarn dev";
      break;
    default:
      doneMessage += `
  ${pkgManager} install`;
      doneMessage += `
  ${pkgManager} run dev`;
      break;
  }
  prompts.outro(doneMessage);
}
function formatTargetDir(targetDir) {
  return targetDir.trim().replace(/\/+$/g, "");
}
function copy(src, dest) {
  const stat = fs.statSync(src);
  if (stat.isDirectory()) {
    copyDir(src, dest);
  } else {
    fs.copyFileSync(src, dest);
  }
}
function isValidPackageName(projectName) {
  return /^(?:@[a-z\d\-*~][a-z\d\-*._~]*\/)?[a-z\d\-~][a-z\d\-._~]*$/.test(
    projectName
  );
}
function toValidPackageName(projectName) {
  return projectName.trim().toLowerCase().replace(/\s+/g, "-").replace(/^[._]/, "").replace(/[^a-z\d\-~]+/g, "-");
}
function copyDir(srcDir, destDir) {
  fs.mkdirSync(destDir, { recursive: true });
  for (const file of fs.readdirSync(srcDir)) {
    const srcFile = path.resolve(srcDir, file);
    const destFile = path.resolve(destDir, file);
    copy(srcFile, destFile);
  }
}
function isEmpty(path2) {
  const files = fs.readdirSync(path2);
  return files.length === 0 || files.length === 1 && files[0] === ".git";
}
function emptyDir(dir) {
  if (!fs.existsSync(dir)) {
    return;
  }
  for (const file of fs.readdirSync(dir)) {
    if (file === ".git") {
      continue;
    }
    fs.rmSync(path.resolve(dir, file), { recursive: true, force: true });
  }
}
function pkgFromUserAgent(userAgent) {
  if (!userAgent) return void 0;
  const pkgSpec = userAgent.split(" ")[0];
  const pkgSpecArr = pkgSpec.split("/");
  return {
    name: pkgSpecArr[0],
    version: pkgSpecArr[1]
  };
}
init().catch((e) => {
  console.error(e);
});
