<script setup lang="ts">
const api = useApi()

const {
  // main query properties
  state,
  asyncStatus,
  refresh,
  refetch,
  // convenient aliases
  error,
  data,
  status,
  isLoading,
  isPending,
  isPlaceholderData,
} = useQuery({
  key: ['todos'],
  query: () => api('/api/library/book/{id}', {
    cache: 'no-cache',
    path: {
      id: '1',
    },
  }),
})
</script>

<template>
  <div>
    <h1>Welcome to Nuxt 4!</h1>
    <p>This is the home page.</p>
    {{ data }}
  </div>
</template>